import { Component, OnInit } from '@angular/core';
import {FormGroup, FormControl} from '@angular/forms'
import {ActivatedRoute} from '@angular/router'
import {RestoService} from '../resto.service'

@Component({
  selector: 'app-update-resto',
  templateUrl: './update-resto.component.html',
  styleUrls: ['./update-resto.component.css']
})
export class UpdateRestoComponent {
alert:boolean=false
  editResto = new FormGroup({
    name: new FormControl(''),
    email: new FormControl(''),
    address: new FormControl('')
  })
  constructor(private router: ActivatedRoute, private resto:RestoService) {}

  ngOnInt(): void{
  console.log(this.router.snapshot.params['id'])
  this.resto.getCurrentResto(this.router.snapshot.params['id']).subscribe((result:any) =>{
    console.log(result)
    this.editResto = new FormGroup({
      name: new FormControl(result['name']),
      email: new FormControl(result['email']),
      address: new FormControl(result['address'])
    })
  })
  }

  collection()
  {
    console.log("item", this.editResto.value)
    this.resto.updateResto(this.router.snapshot.params['id'],this.editResto.value).subscribe((result)=>{
      console.log("result",result)
      this.alert=true
    })
  }
  closeAlert()
  {
    this.alert=false
  }
}
<h1>Edit Restaurant</h1>


<div *ngIf="alert" class="alert alert-success alert-dismissible fade show" role="alert">
    <strong>Success!</strong> Your data has been updated.
    <button (click)="closeAlert()" type="button" class="close" data-dismiss="alert" aria-label="Close">
      <span aria-hidden="true">&times;</span>
    </button>
  </div>



<form [formGroup]="editResto" (ngSubmit)="collection()" >
    <div class="form-group">
        <label>Name</label>

        <input type="text" class="form-control" placeholder="Enter Name" formControlName="name">
    </div>
    <div class="form-group">
        <label>Email</label>

        <input type="text" class="form-control" placeholder="Enter Email" formControlName="email">
    </div>
    <div class="form-group">
        <label>Address</label>

        <input type="text" class="form-control" placeholder="Enter Address" formControlName="address">
    </div>


    <button type="submit" class="btn btn-primary mt-2">Update</button>
</form>
